sys_script_include.script:
var NeeditEngine = Class.create();
NeeditEngine.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

	triggerneedittaskrecord : function(sysid,assessors){

	gs.info("Entered NI Script Include");
	var assessorlist = [];
	assessorlist = assessors.split(',');

	for(var i=0;i<assessorlist.length;i++)
	{
	gs.info("Inside NI Script Include");
	var needittask = new GlideRecord('x_58872_needit_needit_task_table');
	needittask.initialize();
	needittask.needit_reference = sysid;
	needittask.assessors = assessorlist[i];
	needittask.insert();
	}
	},
    type: 'NeeditEngine'
});
